import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { insertFundingSourceSchema, insertVirtualCardSchema, insertTransactionSchema, insertMerchantSchema } from "@shared/schema";
import { z } from "zod";
import { nanoid } from "nanoid";
import Stripe from "stripe";
import { stripeIssuingService } from "./services/stripe-issuing";

if (!process.env.STRIPE_SECRET_KEY) {
  throw new Error('Missing required Stripe secret: STRIPE_SECRET_KEY');
}

const stripe = new Stripe(process.env.STRIPE_SECRET_KEY, {
  apiVersion: "2025-06-30.basil",
});

// Helper function to determine card brand from number
function getBrandFromNumber(cardNumber: string): string {
  const cleanNumber = cardNumber.replace(/\s/g, '');
  if (cleanNumber.startsWith('4')) return 'visa';
  if (cleanNumber.startsWith('5')) return 'mastercard';
  if (cleanNumber.startsWith('34') || cleanNumber.startsWith('37')) return 'amex';
  return 'unknown';
}

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Funding sources routes
  app.get('/api/funding-sources', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const sources = await storage.getFundingSourcesByUserId(userId);

      // If no funding sources exist, create sample ones for demo
      if (sources.length === 0) {
        const sampleSources = [
          {
            userId,
            name: "Chase Freedom",
            type: "credit_card",
            last4: "1234",
            expiryMonth: 12,
            expiryYear: 2025,
            brand: "visa",
            defaultSplitPercentage: "60",
            stripePaymentMethodId: `pm_${nanoid(16)}`,
          },
          {
            userId,
            name: "Bank of America",
            type: "debit_card",
            last4: "5678",
            expiryMonth: 8,
            expiryYear: 2026,
            brand: "mastercard",
            defaultSplitPercentage: "40",
            stripePaymentMethodId: `pm_${nanoid(16)}`,
          }
        ];

        for (const source of sampleSources) {
          await storage.createFundingSource(source);
        }

        const newSources = await storage.getFundingSourcesByUserId(userId);
        res.json(newSources);
      } else {
        res.json(sources);
      }
    } catch (error) {
      console.error("Error fetching funding sources:", error);
      res.status(500).json({ message: "Failed to fetch funding sources" });
    }
  });

  app.post('/api/funding-sources', isAuthenticated, async (req: any, res) => {
    try {
      console.log("Create funding source request:", req.body);

      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      // Validate required fields
      const { cardNumber, expiryMonth, expiryYear, cvv, name } = req.body;
      if (!cardNumber || !expiryMonth || !expiryYear || !cvv || !name) {
        return res.status(400).json({ 
          message: "Missing required fields: cardNumber, expiryMonth, expiryYear, cvv, name",
          receivedFields: Object.keys(req.body)
        });
      }

      // Create Stripe customer if not exists
      let stripeCustomerId = user.stripeCustomerId;
      if (!stripeCustomerId) {
        const customer = await stripe.customers.create({
          email: user.email || undefined,
          name: `${user.firstName || ''} ${user.lastName || ''}`.trim() || undefined,
        });
        stripeCustomerId = customer.id;
        await storage.updateUserStripeInfo(userId, stripeCustomerId, "");
      }

      // For development/demo, create a mock payment method entry
      // In production, this should use Stripe Elements for secure tokenization
      let paymentMethod;

      if (process.env.NODE_ENV === 'development') {
        // Generate a mock payment method ID for development
        const mockPaymentMethodId = `pm_mock_${nanoid(16)}`;

        // Create a mock payment method object for development
        paymentMethod = {
          id: mockPaymentMethodId,
          type: 'card',
          card: {
            brand: getBrandFromNumber(cardNumber),
            last4: cardNumber.slice(-4)
          }
        };

        console.log('Created mock payment method for development:', mockPaymentMethodId);
      } else {
        // In production, this would use payment method tokens from Stripe Elements
        throw new Error('Direct card creation not supported in production. Use Stripe Elements.');
      }

      // Skip Stripe attachment in development mode since we're using mock payment methods
      if (process.env.NODE_ENV !== 'development') {
        // Attach payment method to customer (only in production with real payment methods)
        await stripe.paymentMethods.attach(paymentMethod.id, {
          customer: stripeCustomerId,
        });
      }

      // Use user input for display, test payment method for Stripe
      const validatedData = insertFundingSourceSchema.parse({
        userId,
        name: req.body.name || `${req.body.brand?.toUpperCase() || 'CARD'} ‚Ä¢‚Ä¢‚Ä¢‚Ä¢${req.body.last4 || cardNumber.slice(-4)}`,
        type: req.body.type || 'credit_card',
        last4: req.body.last4 || cardNumber.slice(-4),
        brand: req.body.brand || getBrandFromNumber(cardNumber),
        defaultSplitPercentage: req.body.defaultSplitPercentage || 0,
        stripePaymentMethodId: paymentMethod.id,
      });

      const source = await storage.createFundingSource(validatedData);
      res.json(source);
    } catch (error) {
      console.error("Error creating funding source:", error);
      res.status(500).json({ message: "Failed to create funding source" });
    }
  });

  app.put('/api/funding-sources/:id', isAuthenticated, async (req: any, res) => {
    try {
      const id = parseInt(req.params.id);
      const updates = req.body;
      const source = await storage.updateFundingSource(id, updates);
      res.json(source);
    } catch (error) {
      console.error("Error updating funding source:", error);
      res.status(500).json({ message: "Failed to update funding source" });
    }
  });

  app.delete('/api/funding-sources/:id', isAuthenticated, async (req: any, res) => {
    try {
      const id = parseInt(req.params.id);
      await storage.deleteFundingSource(id);
      res.json({ message: "Funding source deleted successfully" });
    } catch (error) {
      console.error("Error deleting funding source:", error);
      res.status(500).json({ message: "Failed to delete funding source" });
    }
  });

  // Virtual cards routes
  app.get('/api/virtual-cards', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const cards = await storage.getVirtualCardsByUserId(userId);

      // If no virtual cards exist, create a sample one for demo
      if (cards.length === 0) {
        const sampleCard = {
          userId,
          name: "bpay Virtual Card",
          balance: "1000.00",
          cardNumber: `4532${Math.floor(Math.random() * 1000000000000).toString().padStart(12, '0')}`,
          expiryMonth: 12,
          expiryYear: 2025,
          cvv: "123",
          status: "active",
          merchantRestrictions: "",
          stripeCardId: `card_${nanoid(16)}`,
        };

        await storage.createVirtualCard(sampleCard);

        const newCards = await storage.getVirtualCardsByUserId(userId);
        res.json(newCards);
      } else {
        res.json(cards);
      }
    } catch (error) {
      console.error("Error fetching virtual cards:", error);
      res.status(500).json({ message: "Failed to fetch virtual cards" });
    }
  });

  app.post('/api/virtual-cards', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      // Create Stripe customer if not exists
      let stripeCustomerId = user.stripeCustomerId;
      if (!stripeCustomerId) {
        const customer = await stripe.customers.create({
          email: user.email || undefined,
          name: `${user.firstName || ''} ${user.lastName || ''}`.trim() || undefined,
        });
        stripeCustomerId = customer.id;
        await storage.updateUserStripeInfo(userId, stripeCustomerId, "");
      }

      // Generate mock virtual card details (in production, use Stripe Issuing)
      const cardNumber = `4532${Math.floor(Math.random() * 1000000000000).toString().padStart(12, '0')}`;
      const expiryMonth = Math.floor(Math.random() * 12) + 1;
      const expiryYear = new Date().getFullYear() + Math.floor(Math.random() * 5) + 1;
      const cvv = Math.floor(Math.random() * 900) + 100;

      const validatedData = insertVirtualCardSchema.parse({
        ...req.body,
        userId,
        cardNumber,
        expiryMonth,
        expiryYear,
        cvv: cvv.toString(),
        stripeCardId: `card_${nanoid(16)}`,
      });

      const virtualCard = await storage.createVirtualCard(validatedData);
      res.json(virtualCard);
    } catch (error) {
      console.error("Error creating virtual card:", error);
      res.status(500).json({ message: "Failed to create virtual card" });
    }
  });

  app.put('/api/virtual-cards/:id', isAuthenticated, async (req: any, res) => {
    try {
      const id = parseInt(req.params.id);
      const updates = req.body;
      const card = await storage.updateVirtualCard(id, updates);
      res.json(card);
    } catch (error) {
      console.error("Error updating virtual card:", error);
      res.status(500).json({ message: "Failed to update virtual card" });
    }
  });

  app.delete('/api/virtual-cards/:id', isAuthenticated, async (req: any, res) => {
    try {
      const id = parseInt(req.params.id);
      await storage.deleteVirtualCard(id);
      res.json({ message: "Virtual card deleted successfully" });
    } catch (error) {
      console.error("Error deleting virtual card:", error);
      res.status(500).json({ message: "Failed to delete virtual card" });
    }
  });

  // Transactions routes
  app.get('/api/transactions', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const transactions = await storage.getTransactionsByUserId(userId);
      res.json(transactions);
    } catch (error) {
      console.error("Error fetching transactions:", error);
      res.status(500).json({ message: "Failed to fetch transactions" });
    }
  });

  app.post('/api/transactions', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const validatedData = insertTransactionSchema.parse({
        ...req.body,
        userId,
      });
      const transaction = await storage.createTransaction(validatedData);
      res.json(transaction);
    } catch (error) {
      console.error("Error creating transaction:", error);
      res.status(500).json({ message: "Failed to create transaction" });
    }
  });

  // Merchants routes
  app.get('/api/merchants', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const merchants = await storage.getMerchantsByUserId(userId);
      res.json(merchants);
    } catch (error) {
      console.error("Error fetching merchants:", error);
      res.status(500).json({ message: "Failed to fetch merchants" });
    }
  });

  app.post('/api/merchants', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const apiKey = `bpay_${nanoid(32)}`;

      const validatedData = insertMerchantSchema.parse({
        ...req.body,
        userId,
        apiKey,
      });

      const merchant = await storage.createMerchant(validatedData);
      res.json(merchant);
    } catch (error) {
      console.error("Error creating merchant:", error);
      res.status(500).json({ message: "Failed to create merchant" });
    }
  });

  // Admin routes
  app.get('/api/admin/users', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (user?.role !== 'admin') {
        return res.status(403).json({ message: "Admin access required" });
      }

      const users = await storage.getAllUsers();
      res.json(users);
    } catch (error) {
      console.error("Error fetching users:", error);
      res.status(500).json({ message: "Failed to fetch users" });
    }
  });

  app.get('/api/admin/transactions', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (user?.role !== 'admin') {
        return res.status(403).json({ message: "Admin access required" });
      }

      const transactions = await storage.getAllTransactions();
      res.json(transactions);
    } catch (error) {
      console.error("Error fetching transactions:", error);
      res.status(500).json({ message: "Failed to fetch transactions" });
    }
  });

  app.get('/api/admin/merchants', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (user?.role !== 'admin') {
        return res.status(403).json({ message: "Admin access required" });
      }

      const merchants = await storage.getAllMerchants();
      res.json(merchants);
    } catch (error) {
      console.error("Error fetching merchants:", error);
      res.status(500).json({ message: "Failed to fetch merchants" });
    }
  });

  // Payment splitting endpoint
  app.post('/api/process-payment', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { amount, merchant, virtualCardId, splits } = req.body;

      // Debug logging
      console.log("Process payment request:", {
        amount,
        merchant,
        virtualCardId,
        splits: splits,
        splitsType: typeof splits,
        splitsIsArray: Array.isArray(splits)
      });

      const user = await storage.getUser(userId);

      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      // Validate that splits is an array
      if (!Array.isArray(splits)) {
        return res.status(400).json({ 
          message: "Invalid splits data - must be an array",
          receivedType: typeof splits,
          receivedData: splits 
        });
      }

      // Process payment splits (demo mode - mock Stripe calls)
      const paymentIntents = [];
      const totalAmount = parseFloat(amount);
      const feePercentage = 0.029; // 2.9% bpay fee
      const totalFees = totalAmount * feePercentage;
      const totalWithFees = totalAmount + totalFees;

      // Mock payment processing for demo
      for (const split of splits) {
        const splitAmount = (totalWithFees * split.percentage) / 100;

        // Simulate payment intent creation with mock data
        const mockPaymentIntent = {
          id: `pi_${nanoid(16)}`,
          amount: splitAmount,
          status: 'succeeded',
          fundingSourceId: split.fundingSourceId
        };

        paymentIntents.push(mockPaymentIntent);

        // Add a small delay to simulate network request
        await new Promise(resolve => setTimeout(resolve, 100));
      }

      // Validate splits total to 100%
      const totalPercentage = splits.reduce((sum: number, split: any) => sum + split.percentage, 0);
      if (Math.abs(totalPercentage - 100) > 0.01) {
        return res.status(400).json({ message: "Split percentages must total 100%" });
      }

      // Create transaction record
      const transaction = await storage.createTransaction({
        userId,
        virtualCardId,
        merchant,
        amount: totalAmount.toFixed(2),
        splits: JSON.stringify(splits),
        status: 'completed',
        fees: totalFees.toFixed(2),
        stripePaymentIntentId: paymentIntents[0]?.id,
      });

      res.json({
        transaction,
        paymentIntents,
        totalAmount,
        totalFees,
        message: "Payment processed successfully"
      });
    } catch (error) {
      console.error("Error processing payment:", error);
      res.status(500).json({ message: "Failed to process payment" });
    }
  });

  // Create payment intent for checkout
  app.post('/api/create-payment-intent', isAuthenticated, async (req: any, res) => {
    try {
      const { amount, merchant, splits } = req.body;
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      const totalAmount = parseFloat(amount);
      const feePercentage = 0.029; // 2.9% bpay fee
      const totalFees = totalAmount * feePercentage;
      const totalWithFees = totalAmount + totalFees;

      // Mock payment intent for demo
      const paymentIntent = {
        client_secret: `pi_${nanoid(16)}_secret_${nanoid(16)}`,
        id: `pi_${nanoid(16)}`,
        amount: Math.round(totalWithFees * 100),
        currency: 'usd',
        status: 'requires_payment_method',
        metadata: {
          merchant,
          originalAmount: amount,
          fees: totalFees.toFixed(2),
          splits: JSON.stringify(splits)
        }
      };

      res.json({ 
        clientSecret: paymentIntent.client_secret,
        totalAmount,
        totalFees,
        totalWithFees
      });
    } catch (error) {
      console.error("Error creating payment intent:", error);
      res.status(500).json({ message: "Failed to create payment intent" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  fundingSources,
  virtualCards,
  transactions,
  merchants,
  type User,
  type UpsertUser,
  type FundingSource,
  type InsertFundingSource,
  type VirtualCard,
  type InsertVirtualCard,
  type Transaction,
  type InsertTransaction,
  type Merchant,
  type InsertMerchant,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and } from "drizzle-orm";

export interface IStorage {
  // User operations (required for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;
  updateUserStripeInfo(userId: string, stripeCustomerId: string, stripeSubscriptionId: string): Promise<User>;

  // Funding sources
  getFundingSourcesByUserId(userId: string): Promise<FundingSource[]>;
  createFundingSource(source: InsertFundingSource): Promise<FundingSource>;
  updateFundingSource(id: number, updates: Partial<InsertFundingSource>): Promise<FundingSource>;
  deleteFundingSource(id: number): Promise<void>;

  // Virtual cards
  getVirtualCardsByUserId(userId: string): Promise<VirtualCard[]>;
  createVirtualCard(card: InsertVirtualCard): Promise<VirtualCard>;
  updateVirtualCard(id: number, updates: Partial<InsertVirtualCard>): Promise<VirtualCard>;
  deleteVirtualCard(id: number): Promise<void>;

  // Transactions
  getTransactionsByUserId(userId: string): Promise<Transaction[]>;
  createTransaction(transaction: InsertTransaction): Promise<Transaction>;
  updateTransaction(id: number, updates: Partial<InsertTransaction>): Promise<Transaction>;

  // Merchants
  getMerchantsByUserId(userId: string): Promise<Merchant[]>;
  createMerchant(merchant: InsertMerchant): Promise<Merchant>;
  updateMerchant(id: number, updates: Partial<InsertMerchant>): Promise<Merchant>;

  // Admin operations
  getAllUsers(): Promise<User[]>;
  getAllTransactions(): Promise<Transaction[]>;
  getAllMerchants(): Promise<Merchant[]>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  async updateUserStripeInfo(userId: string, stripeCustomerId: string, stripeSubscriptionId: string): Promise<User> {
    const [user] = await db
      .update(users)
      .set({
        stripeCustomerId,
        stripeSubscriptionId,
        updatedAt: new Date(),
      })
      .where(eq(users.id, userId))
      .returning();
    return user;
  }

  // Funding sources
  async getFundingSourcesByUserId(userId: string): Promise<FundingSource[]> {
    return await db
      .select()
      .from(fundingSources)
      .where(eq(fundingSources.userId, userId))
      .orderBy(desc(fundingSources.createdAt));
  }

  async createFundingSource(source: InsertFundingSource): Promise<FundingSource> {
    const [newSource] = await db
      .insert(fundingSources)
      .values(source)
      .returning();
    return newSource;
  }

  async updateFundingSource(id: number, updates: Partial<InsertFundingSource>): Promise<FundingSource> {
    const [updatedSource] = await db
      .update(fundingSources)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(fundingSources.id, id))
      .returning();
    return updatedSource;
  }

  async deleteFundingSource(id: number): Promise<void> {
    await db.delete(fundingSources).where(eq(fundingSources.id, id));
  }

  // Virtual cards
  async getVirtualCardsByUserId(userId: string): Promise<VirtualCard[]> {
    return await db
      .select()
      .from(virtualCards)
      .where(eq(virtualCards.userId, userId))
      .orderBy(desc(virtualCards.createdAt));
  }

  async createVirtualCard(card: InsertVirtualCard): Promise<VirtualCard> {
    const [newCard] = await db
      .insert(virtualCards)
      .values(card)
      .returning();
    return newCard;
  }

  async updateVirtualCard(id: number, updates: Partial<InsertVirtualCard>): Promise<VirtualCard> {
    const [updatedCard] = await db
      .update(virtualCards)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(virtualCards.id, id))
      .returning();
    return updatedCard;
  }

  async deleteVirtualCard(id: number): Promise<void> {
    await db.delete(virtualCards).where(eq(virtualCards.id, id));
  }

  // Transactions
  async getTransactionsByUserId(userId: string): Promise<Transaction[]> {
    return await db
      .select()
      .from(transactions)
      .where(eq(transactions.userId, userId))
      .orderBy(desc(transactions.createdAt));
  }

  async createTransaction(transaction: InsertTransaction): Promise<Transaction> {
    const [newTransaction] = await db
      .insert(transactions)
      .values(transaction)
      .returning();
    return newTransaction;
  }

  async updateTransaction(id: number, updates: Partial<InsertTransaction>): Promise<Transaction> {
    const [updatedTransaction] = await db
      .update(transactions)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(transactions.id, id))
      .returning();
    return updatedTransaction;
  }

  // Merchants
  async getMerchantsByUserId(userId: string): Promise<Merchant[]> {
    return await db
      .select()
      .from(merchants)
      .where(eq(merchants.userId, userId))
      .orderBy(desc(merchants.createdAt));
  }

  async createMerchant(merchant: InsertMerchant): Promise<Merchant> {
    const [newMerchant] = await db
      .insert(merchants)
      .values(merchant)
      .returning();
    return newMerchant;
  }

  async updateMerchant(id: number, updates: Partial<InsertMerchant>): Promise<Merchant> {
    const [updatedMerchant] = await db
      .update(merchants)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(merchants.id, id))
      .returning();
    return updatedMerchant;
  }

  // Admin operations
  async getAllUsers(): Promise<User[]> {
    return await db.select().from(users).orderBy(desc(users.createdAt));
  }

  async getAllTransactions(): Promise<Transaction[]> {
    return await db.select().from(transactions).orderBy(desc(transactions.createdAt));
  }

  async getAllMerchants(): Promise<Merchant[]> {
    return await db.select().from(merchants).orderBy(desc(merchants.createdAt));
  }
}

export const storage = new DatabaseStorage();

