client/src/hooks/useDemoMode.ts.content:
import { useState, useEffect, createContext, useContext, ReactNode } from "react";

interface DemoModeContextType {
  isDemoMode: boolean;
  toggleDemoMode: () => void;
}

const DemoModeContext = createContext<DemoModeContextType | undefined>(undefined);

export const useDemoMode = () => {
  const context = useContext(DemoModeContext);
  if (!context) {
    throw new Error("useDemoMode must be used within a DemoModeProvider");
  }
  return context;
};

export const DemoModeProvider = ({ children }: { children: ReactNode }) => {
  const [isDemoMode, setIsDemoMode] = useState(() => {
    // Initialize from localStorage if available
    const saved = localStorage.getItem('bpay-demo-mode');
    return saved ? JSON.parse(saved) : false;
  });

  const toggleDemoMode = () => {
    setIsDemoMode(prev => {
      const newValue = !prev;
      localStorage.setItem('bpay-demo-mode', JSON.stringify(newValue));
      return newValue;
    });
  };

  useEffect(() => {
    // Save to localStorage whenever demo mode changes
    localStorage.setItem('bpay-demo-mode', JSON.stringify(isDemoMode));
  }, [isDemoMode]);

  return (
    <DemoModeContext.Provider value={{ isDemoMode, toggleDemoMode }}>
      {children}
    </DemoModeContext.Provider>
  );
};
